import React from 'react'
import './App.css';
import '@tomtom-international/web-sdk-maps/dist/maps.css'
import * as ttapi from '@tomtom-international/web-sdk-services'
import * as tt from '@tomtom-international/web-sdk-maps';
import { useRef,useState,useEffect } from 'react';
// import { destinations } from './constants';

function App() {

  const mapElement = useRef();

  const [map, setMap] = useState({});

  const [longitude, setLongitude] = useState(77.5747463);
  const [latitude, setLatitude] = useState(12.9140182);

  const convertToPoints = (lngLat) => {
    return {
      point: {
        latitude: lngLat.lat,
        longitude: lngLat.lng
      }
    }
  }

  const addDeliveryMarker = (lngLat, map) => {
    const element = document.createElement('div')
    element.className = 'marker-delivery'
    new tt.Marker({
      element: element
    }).setLngLat(lngLat).addTo(map)
  }


  const drawRoute = (geoJson, map) => {
    if (map.getLayer('route')) {
      map.removeLayer('route')
      map.removeSource('route')
    }
    map.addLayer({
      id: 'route',
      type: 'line',
      source: {
        type: 'geojson',
        data: geoJson
      },
      paint: {
        'line-color': '#4a90e2',
        'line-width': 6
      }
    })
  }

  // const destinations = [
  //   {'lng': 12.9140182, 'lat': 77.5747463}, {'lng': 12.9414398, 'lat': 77.5454111}, {'lng': 12.91593975, 'lat': 77.5858407}, {'lng': 12.9414398, 'lat': 77.5454111}, {'lng': 12.8818289, 'lat': 77.5728281}, {'lng': 12.89823678, 'lat': 77.58827011}, {'lng': 12.913448, 'lat': 77.5855807}, {'lng': 12.90446756, 'lat': 77.58577882}
  // ]
  useEffect(() => {
    const origin = {
      lng: longitude,
      lat: latitude,
    }

    let map = tt.map({
      key: "y1Duis78asfADSQn9HYekaSyUw4siXLf",
      container: mapElement.current,
      stylesVisibility: {
        trafficIncidents: true,
        trafficFlow: true
      },
      center: [longitude, latitude],
      zoom: 14
    });
    setMap(map);

    const addMarker = () => {
      const element = document.createElement('div')
      element.className = 'marker'
      const marker = new tt.Marker({
        element: element,
      }).setLngLat([longitude,latitude])
      .addTo(map)
    }
    addMarker();


    const sortDestinations = (locations) => {
      const pointsForDestinations = locations.map((destination) => {
        return convertToPoints(destination)
      })
      const callParameters = {
        key:"y1Duis78asfADSQn9HYekaSyUw4siXLf",
        destinations: pointsForDestinations,
        origins: [convertToPoints(origin)],
      }

    return new Promise((resolve, reject) => {
      ttapi.services
        .matrixRouting(callParameters)
        .then((matrixAPIResults) => {
          const results = matrixAPIResults.matrix[0]
          const resultsArray = results.map((result, index) => {
            return {
              location: locations[index],
              drivingtime: result.response.routeSummary.travelTimeInSeconds,
            }
          })
          resultsArray.sort((a, b) => {
            return a.drivingtime - b.drivingtime
          })
          const sortedLocations = resultsArray.map((result) => {
            return result.location
          })
          resolve(sortedLocations)
        })
      })
    }


    const recalculateRoutes = () => {
      sortDestinations(destinations).then((sorted) => {
        sorted.unshift(origin)

        ttapi.services
          .calculateRoute({
            key: "y1Duis78asfADSQn9HYekaSyUw4siXLf",
            locations: sorted,
          })
          .then((routeData) => {
            const geoJson = routeData.toGeoJson()
            console.log(geoJson)
            drawRoute(geoJson, map)
        })
      })
    }

    for (let d in destinations) {
      console.log(destinations[d]);
      addDeliveryMarker(destinations[d],map);
    }
    recalculateRoutes()
    map.on('click', (e) => {
      destinations.push(e.lngLat)
      console.log(e.lngLat)
      addDeliveryMarker(e.lngLat, map)
      recalculateRoutes()
    })

    return () => map.remove()
    // callParameters = {
    //   key: "y1Duis78asfADSQn9HYekaSyUw4siXLf",
    //   destinations: pointsForDestinations,
    //   origins: [convertToPoints(origin)],
    // }
    // return new Promise((resolve, reject) => {
    // })

  }, [longitude,latitude]);


  return (
    <div className="App">
      GROW SIMPLEE
      <div className="map" ref={mapElement}></div>
    </div>
  );
}

export default App;
