import React from 'react'
import "@tomtom-international/web-sdk-maps/dist/maps.css";
import * as ttservices from "@tomtom-international/web-sdk-services";
import tt from "@tomtom-international/web-sdk-maps";
import { useEffect, useState, useRef } from "react";
import { api_key } from "constants.js"
import "../../App.css"

function Map({ currLocation, deliveryLocation, destinations, zoom_level, travel_mode }) {
    const mapElement = useRef();
    const [map, setMap] = useState(null); 
    const [waypoints,setWayPoints] = useState(destinations);

    function create_delivery_marker(location) {
        const marker_el = document.createElement("div");
        marker_el.className = 'marker-delivery';
        // const popup = new tt.Popup({ offset: 20 }).setText(location.name);
        const marker = new tt.Marker({ element: marker_el, anchor: "bottom" })
        .setLngLat([location.lng, location.lat])
        .addTo(map);

        return marker;
    }

    const create_route = (locations) => {
      ttservices.services
        .calculateRoute({
          key: api_key,
          locations,
        })
        .then((routeData) => {
          const features = routeData.toGeoJson().features;
          features.forEach((feature, index) => {
            map.addLayer({
              id: "route" + index,
              type: "line",
              source: {
                type: "geojson",
                data: feature,
              },
              paint: {
                "line-color": `red`,
                "line-opacity": 0.8,
                "line-width": 6,
                "line-dasharray": [1, 0, 1, 0],
              }
            });
          });
        });
    };

    useEffect(() => {
        let map = tt.map({
        key: api_key,
        container: mapElement.current,
        center: [77.5747463,12.9140182],
        zoom: zoom_level,
        });
        map.addControl(new tt.FullscreenControl());
        map.addControl(new tt.NavigationControl());
        setMap(map);
        return () => map.remove();
    }, []);

    useEffect(() => {
        if (map) {
          map.on("load", () => {
            waypoints.forEach((location) => {
            create_delivery_marker(location);
              });
            const locations = waypoints.map((location) => [location.lng,location.lat])
            for(let i=0;i<locations.length;)
            create_route(locations.slice(0,150))
          });
        }
        else{
            console.log("error loading map"); //Add better error handling function
        }
      },);

    return (
      <div className="map_wrapper">
        <div 
          ref={mapElement} 
          style={{
            position: 'absolute',
            top: 0,
            bottom: 0,
            left: 0,
            right: 0,
            width: '100%',
            height: '100%'
          }}
        />
      </div>
    );
  }
  export default Map;
