import React from "react";

import Maps from "views/admin/Maps.js";

export default function Admin(props) {
  const {role,userId} = props;
  const [openTab, setOpenTab] = React.useState(1);
  const [driverId,setDriverId] = React.useState(0);
  const [destination,setDestinations] = React.useState([]);

  const handleChangeDriverId = (e) => {
    setDriverId(e.target.value)
    //fetchAllDestinations(driverId); 
  }
  return (
    <>
      {/* <Sidebar /> */}
      {/* <div className="relative md:ml-64 bg-blueGray-100"> */}
        {/* <AdminNavbar /> */}
        {/* Header */}
        {/* <HeaderStats />
        <div className="p-4 md:p-10 mx-auto w-full -m-24"> */}
          {/* <Switch>
            <Route path="/admin/dashboard" exact component={Dashboard} />
            <Route path="/admin/maps" exact component={Maps} />
            <Route path="/admin/settings" exact component={Settings} />
            <Route path="/admin/tables" exact component={Tables} />
            <Redirect from="/admin" to="/admin/dashboard" />
          </Switch> */}
          {/* <FooterAdmin /> */}
        {/* </div> */}
      {/* </div> */}
      <div className="flex flex-wrap">
        <div className="w-full">
          <ul
            className="flex mb-0 list-none flex-wrap pt-3 pb-4 flex-row"
            role="tablist"
          >
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 1
                    ? "text-white bg-lightBlue-600"
                    : "text-lightBlue-600 bg-white")
                }
                onClick={e => {
                  e.preventDefault();
                  setOpenTab(1);
                }}
                data-toggle="tab"
                href="#link1"
                role="tablist"
              >
                <i className="fas fa-space-shuttle text-base mr-1"></i> Map
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 2
                    ? "text-white bg-lightBlue-600"
                    : "text-lightBlue-600 bg-white")
                }
                onClick={e => {
                  e.preventDefault();
                  setOpenTab(2);
                }}
                data-toggle="tab"
                href="#link2"
                role="tablist"
              >
                <i className="fas fa-cog text-base mr-1"></i>  Destinations
              </a>
            </li>
          </ul>
          <div class="mb-3 pt-0 flex">
            <input type="text" placeholder="Driver User Id" class="px-2 py-1 placeholder-blueGray-300 text-blueGray-600 relative bg-white bg-white rounded text-sm shadow outline-none focus:outline-none focus:shadow-outline grow" value={driverId} onChange={handleChangeDriverId}/>
            <button className="bg-lightBlue-500 text-white active:bg-lightBlue-600 font-bold uppercase text-sm px-6 py-3 rounded-full shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150" type="button">
              Show
            </button>
          </div>
          <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
            <div className="px-4 py-5 flex-auto">
              <div className="tab-content tab-space">
                <div className={openTab === 1 ? "block" : "hidden"} id="link1">
                  <Maps />
                </div>
                <div className={openTab === 2 ? "block" : "hidden"} id="link2">
                  List of Destinations
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
