import React from "react";
import { useState, useEffect } from 'react';
import Map from "views/driver/Map.js";
import DestinationList from "./DestinationsList";
import OTPModal from "./OTPmodal";
import { driverDestinations } from "constants.js";

export default function Driver(props) {
  const {role,userId} = props;
  const [openTab, setOpenTab] = React.useState(1);

  const [currLocation,setCurrLocatoin] = useState({lat: 12.9140182, lng: 77.5747463});
  const [deliveryLocation,setDeliveryLocation] = useState({lat: 12.9414398, lng: 77.5454111, locationId:1});
  const [showOTPModal, setShowOTPModal] = React.useState(false);
  const [destinations,setDestinations] = useState([]);
  const [completedDest,setCompletedDest] = useState([]);

  const handleDelivery = () => {
    setShowOTPModal("true");
  }

  useEffect(() => {
    // fetch destination list
    setDestinations(driverDestinations.map((dest,i) => {
      return({
        ...dest,
        locationId: i+1
      })
    }))
    setDeliveryLocation({...driverDestinations[1],locationId:1});
  },[])
  return (
      <div className="flex flex-wrap">
        <div className="w-full">
          <ul
            className="flex mb-0 list-none flex-wrap pt-3 pb-4 flex-row"
            role="tablist"
          >
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 1
                    ? "text-white bg-lightBlue-600"
                    : "text-lightBlue-600 bg-white")
                }
                onClick={e => {
                  e.preventDefault();
                  setOpenTab(1);
                }}
                data-toggle="tab"
                href="#link1"
                role="tablist"
              >
                <i className="fas fa-space-shuttle text-base mr-1"></i> Map
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 2
                    ? "text-white bg-lightBlue-600"
                    : "text-lightBlue-600 bg-white")
                }
                onClick={e => {
                  e.preventDefault();
                  setOpenTab(2);
                }}
                data-toggle="tab"
                href="#link2" 
                role="tablist"
              >
                <i className="fas fa-cog text-base mr-1"></i>  Destinations
              </a>
            </li>
          </ul>
          <button className="bg-lightBlue-500 text-white active:bg-lightBlue-600 font-bold uppercase text-sm px-6 py-3 rounded-full shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150 w-1/4 ml-auto" type="button" onClick={handleDelivery}>
            Deliver
          </button>
          <OTPModal 
            showOTPModal={showOTPModal} 
            setShowOTPModal={setShowOTPModal} 
            destinations={destinations} 
            setDestinations={setDestinations} 
            setDeliveryLocation={setDeliveryLocation}
            completedDest={completedDest}
            setCompletedDest={setCompletedDest}
          />
          <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
            <div className="px-4 py-5 flex-auto">
              <div className="tab-content tab-space">
                <div className={openTab === 1 ? "block" : "hidden"} id="link1">
                  <Map currLocation= {currLocation} deliveryLocation= {deliveryLocation} destinations={destinations} zoom_level={12} travel_mode="truck" />
                </div>
                <div className={openTab === 2 ? "block" : "hidden"} id="link2">
                  <DestinationList />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
  );
}
